import React, { Component, Fragment} from 'react'

import {NUEVO_CLIENTE} from '../mutations';
import {Mutation} from 'react-apollo';

class NuevoCliente extends Component{
    state = {
        cliente:{
            nombre: '',
            apellido: '',
            empresa: '',
            edad: '',
            email: '',
            tipo: '' 
        },
        error: false,
        emails:[]
    }

    nuevoCampo = () => {
        this.setState({
            emails:this.state.emails.concat([{email:''}])
        }) 
    }

    leerCampo = i =>e=>{
        const nuevoEmail = this.state.emails.map((email,index)=> {
            if(i !==index) return index;
            return {
                ...email,
                email: e.target.value
            }
        });
        this.setState({
            emails: nuevoEmail
        })
    }

    quitarCampo = i => ()=> {
        this.setState({
            emails: this.state.emails.filter((email,index)=> 1 !== index)
        })
    }

    render() {
        const {error} = this.state; 
        let respuesta =  (error) ? <p className ="alert alert-danger p-3 text-center">Todos los campos son obligatorios</p>:'';// los dos puntos significa "else"
        return (
            <Fragment>
                <h2 className="text-center">Nuevo Cliente</h2>
                {respuesta}
                <div className="row justify-content-center">
                <Mutation mutation={NUEVO_CLIENTE} onCompleted={() => this.props.history.push('/')
                //-el onCompleted lleva a la pagina principal
                }>
                
                {crearCliente => (
                    <form 
                        className="col-md-8 m-3"
                        onSubmit={e=> {
                            e.preventDefault();//evitar que el formulario se envia apenas se preciona el boton, para revisar que los formularios esten correctos.

                            const {nombre, apellido, empresa, edad, email, tipo} = this.state.cliente;
                            
                            if(nombre ===''||apellido ===''||empresa ===''||edad ===''||email ===''||tipo === ''){
                                this.setState({
                                    error:true
                                });
                                return;
                            }

                            this.setState({
                                error:false
                            })

                            const input = {
                                nombre,
                                apellido,
                                empresa,
                                edad: Number(edad),
                                tipo,
                                email
                            };
                            crearCliente({
                                variables: {input}
                            })
                        }} >
                        <div className="form-row">
                            <div className="form-group col-md-6">
                                <label>Nombre</label>
                                <input 
                                type="text" 
                                className="form-control" 
                                placeholder="Nombre"
                                onChange={e => {
                                    this.setState({
                                        cliente:{
                                            ...this.state.cliente,
                                            nombre:e.target.value
                                        }
                                    })
                                }}/>
                            </div>
                            <div className="form-group col-md-6">
                                <label>Apellido</label>
                                <input 
                                type="text" 
                                className="form-control" 
                                placeholder="Apellido"
                                onChange={e => {
                                    this.setState({
                                        cliente:{
                                            ...this.state.cliente,
                                            apellido:e.target.value
                                        }
                                    })
                                }}/>
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="form-group col-md-12">
                                <label>Empresa</label>
                                <input type="text" 
                                className="form-control" 
                                placeholder="Empresa"
                                onChange={e => {
                                    this.setState({
                                        cliente:{
                                            ...this.state.cliente,
                                            empresa:e.target.value
                                        }
                                    })
                                }}/>
                        </div>
                        {this.state.emails.map((input, index)=> (
                            <div key={index} className="form-group col-md-12">
                                <label>Correo: {index + 1}</label>
                                <div className="input-group">
                                    <input
                                        onChange={this.leerCampo(index)}
                                        type="email"
                                        placeholder="Email"
                                        className="form-control" 
                                        />
                                        <div className="input-group-append">
                                            <button type="button" className="btn btn-danger" onClick={this.quitarCampo(index)}>
                                                &times; Eliminar 
                                            </button>
                                        </div>
                                    </div>
                            </div>
                        ))}
                        <div className="form-group d-flex justify-content-center col-md-12">
                                <button 
                                type="button" 
                                className="btn btn-warning" 
                                onClick={this.nuevoCampo}>+ Agregar Email
                                </button>
                        </div>
                            <div className="form-group col-md-6">
                                <label>Email</label>
                                <input type="text" 
                                className="form-control" 
                                placeholder="Email"
                                onChange={e => {
                                    this.setState({
                                        cliente:{
                                            ...this.state.cliente,
                                            email:e.target.value
                                        }
                                    })
                                }}/>
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="form-group col-md-6">
                                <label>Edad</label>
                                <input type="text" 
                                className="form-control" 
                                placeholder="Edad"
                                onChange={e => {
                                    this.setState({
                                        cliente:{
                                            ...this.state.cliente,
                                            edad:e.target.value
                                        }
                                    })
                                }}/>
                            </div>
                            <div className="form-group col-md-6">
                                <label>Tipo Cliente</label>  
                                <select onChange={e => {
                                    this.setState({
                                        cliente:{
                                            ...this.state.cliente,
                                            tipo:e.target.value
                                        }
                                    })
                                }}
                                className="form-control">
                                    <option value="">Elegir...</option>
                                    <option value="PREMIUM">PREMIUM</option>
                                    <option value="BASICO">B√ÅSICO</option>
                                </select>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-success float-right">Agregar Cliente</button>
                    </form>
                    )  }
                </Mutation>
                </div>
            </Fragment>
        );
    }
}

export default NuevoCliente;
